'use strict';

const OperationBase = require('mongodb/lib/operations/operation').OperationBase;
const indexInformation = require('mongodb/lib/operations/common_functions').indexInformation;

class IndexInformationOperation extends OperationBase {
  constructor(db, name, options) {
    super(options);

    this.db = db;
    this.name = name;
  }

  execute(callback) {
    const db = this.db;
    const name = this.name;
    const options = this.options;

    indexInformation(db, name, options, callback);
  }
}

module.exports = IndexInformationOperation;
